import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import AttendanceFormPage from './pages/AttendanceFormPage';
import AttendanceDashboardPage from './pages/AttendanceDashboardPage';
import './styles/App.css';

function Navigation() {
  const location = useLocation();

  return (
    <nav className="app-nav">
      <Link 
        to="/" 
        className={location.pathname === '/' ? 'active' : ''}
      >
        üìù Mark Attendance
      </Link>
      <Link 
        to="/dashboard" 
        className={location.pathname === '/dashboard' ? 'active' : ''}
      >
        üìä View Dashboard
      </Link>
    </nav>
  );
}

function App() {
  return (
    <Router>
      <div className="App">
        <header className="app-header">
          <h1>üè¢ Employee Attendance Tracker</h1>
          <p>HR Management System - Limkokwing University</p>
        </header>

        <Navigation />

        <main className="app-main">
          <Routes>
            <Route path="/" element={<AttendanceFormPage />} />
            <Route path="/dashboard" element={<AttendanceDashboardPage />} />
          </Routes>
        </main>

        <footer className="app-footer">
          <p>BIWA2110 Web Application Development - Lab Test 2</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
