import React, { useState } from 'react';
import AttendanceForm from './components/AttendanceForm';
import AttendanceDashboard from './components/AttendanceDashboard';
import './styles/App.css';

function App() {
  const [activeTab, setActiveTab] = useState('form');
  const [refreshKey, setRefreshKey] = useState(0);

  const handleAttendanceAdded = () => {
    // Trigger refresh of dashboard when new attendance is added
    setRefreshKey(prev => prev + 1);
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>üè¢ Employee Attendance Tracker</h1>
        <p>HR Management System - Limkokwing University</p>
      </header>

      <nav className="app-nav">
        <button 
          className={activeTab === 'form' ? 'active' : ''}
          onClick={() => setActiveTab('form')}
        >
          üìù Mark Attendance
        </button>
        <button 
          className={activeTab === 'dashboard' ? 'active' : ''}
          onClick={() => setActiveTab('dashboard')}
        >
          üìä View Dashboard
        </button>
      </nav>

      <main className="app-main">
        {activeTab === 'form' && (
          <AttendanceForm onAttendanceAdded={handleAttendanceAdded} />
        )}
        {activeTab === 'dashboard' && (
          <AttendanceDashboard key={refreshKey} />
        )}
      </main>

      <footer className="app-footer">
        <p>BIWA2110 Web Application Development - Lab Test 2</p>
      </footer>
    </div>
  );
}

export default App;
