import React from 'react';
import { useNavigate } from 'react-router-dom';
import AttendanceForm from '../components/AttendanceForm';

const AttendanceFormPage = () => {
  const navigate = useNavigate();

  const handleAttendanceAdded = () => {
    // Optional: Show success message or redirect
    console.log('Attendance recorded successfully!');
  };

  const goToDashboard = () => {
    navigate('/dashboard');
  };

  return (
    <div className="page-container">
      <div className="page-header">
        <h2>Mark Employee Attendance</h2>
        <p>Record daily attendance for employees</p>
        <button 
          className="secondary-btn"
          onClick={goToDashboard}
        >
          ðŸ“Š View Attendance Dashboard
        </button>
      </div>

      <div className="page-content">
        <AttendanceForm onAttendanceAdded={handleAttendanceAdded} />
      </div>
    </div>
  );
};

export default AttendanceFormPage;
